function RageUIPolice.SetScaleformParams(scaleform, data)
    data = data or {}
    for k, v in pairs(data) do
        PushScaleformMovieFunction(scaleform, v.name)
        if v.param then
            for _, par in pairs(v.param) do
                if math.type(par) == "integer" then
                    PushScaleformMovieFunctionParameterInt(par)
                elseif type(par) == "boolean" then
                    PushScaleformMovieFunctionParameterBool(par)
                elseif math.type(par) == "float" then
                    PushScaleformMovieFunctionParameterFloat(par)
                elseif type(par) == "string" then
                    PushScaleformMovieFunctionParameterString(par)
                end
            end
        end
        if v.func then
            v.func()
        end
        PopScaleformMovieFunctionVoid()
    end
end

---Visible
---@param Menu function
---@param Value boolean
---@return table
---@public
function RageUIPolice.Visible(Menu, Value)
    if Menu ~= nil then
        if Menu() then
            if type(Value) == "boolean" then
                if Value then
                    if RageUIPolice.CurrentMenu ~= nil then
			if RageUIPolice.CurrentMenu.Closed ~= nil then
                            RageUIPolice.CurrentMenu.Closed()
                        end
                        RageUIPolice.CurrentMenu.Open = not Value
                    end
                    Menu:UpdateInstructionalButtons(Value);
                    Menu:UpdateCursorStyle();
                    RageUIPolice.CurrentMenu = Menu
                    menuOpen = true
                else
                    menuOpen = false
                    RageUIPolice.CurrentMenu = nil
                end
                Menu.Open = Value
                RageUIPolice.Options = 0
                RageUIPolice.ItemOffset = 0
                RageUIPolice.LastControl = false
            else
                return Menu.Open
            end
        end
    end
end

function RageUIPolice.CloseAll()
    menuOpen = false
    if RageUIPolice.CurrentMenu ~= nil then
        local parent = RageUIPolice.CurrentMenu.Parent
        while parent ~= nil do
            parent.Index = 1
            parent.Pagination.Minimum = 1
            parent.Pagination.Maximum = 10
            parent = parent.Parent
        end
        RageUIPolice.CurrentMenu.Index = 1
        RageUIPolice.CurrentMenu.Pagination.Minimum = 1
        RageUIPolice.CurrentMenu.Pagination.Maximum = 10
        RageUIPolice.CurrentMenu.Open = false
        RageUIPolice.CurrentMenu = nil
    end
    RageUIPolice.Options = 0
    RageUIPolice.ItemOffset = 0
    ResetScriptGfxAlign()
end

