---Render
---@param instructionalButton boolean
---@return nil
---@public
function RageUIPolice.Render(instructionalButton)
    if RageUIPolice.CurrentMenu ~= nil then
        if RageUIPolice.CurrentMenu() then
            if RageUIPolice.CurrentMenu.Safezone then
                ResetScriptGfxAlign()
            end
            if (instructionalButton) then
                DrawScaleformMovieFullscreen(RageUIPolice.CurrentMenu.InstructionalScaleform, 255, 255, 255, 255, 0)
            end
            RageUIPolice.CurrentMenu.Options = RageUIPolice.Options
            RageUIPolice.CurrentMenu.SafeZoneSize = nil
            RageUIPolice.Controls()
            RageUIPolice.Options = 0
            RageUIPolice.StatisticPanelCount = 0
            RageUIPolice.ItemOffset = 0
            if RageUIPolice.CurrentMenu.Controls.Back.Enabled and RageUIPolice.CurrentMenu.Closable then
                if RageUIPolice.CurrentMenu.Controls.Back.Pressed then
                    RageUIPolice.CurrentMenu.Controls.Back.Pressed = false
                    local Audio = RageUIPolice.Settings.Audio
                    RageUIPolice.PlaySound(Audio[Audio.Use].Back.audioName, Audio[Audio.Use].Back.audioRef)
                    collectgarbage()
                    if RageUIPolice.CurrentMenu.Closed ~= nil then
                        RageUIPolice.CurrentMenu.Closed()
                    end
                    if RageUIPolice.CurrentMenu.Parent ~= nil then
                        if RageUIPolice.CurrentMenu.Parent() then
                            RageUIPolice.NextMenu = RageUIPolice.CurrentMenu.Parent
                            RageUIPolice.CurrentMenu:UpdateCursorStyle()
                        else
                            --print('xxx') Debug print
                            RageUIPolice.NextMenu = nil
                            RageUIPolice.Visible(RageUIPolice.CurrentMenu, false)
                        end
                    else
                        --print('zz') Debug print
                        RageUIPolice.NextMenu = nil
                        RageUIPolice.Visible(RageUIPolice.CurrentMenu, false)
                    end
                end
            end
            if RageUIPolice.NextMenu ~= nil then
                if RageUIPolice.NextMenu() then
                    RageUIPolice.Visible(RageUIPolice.CurrentMenu, false)
                    RageUIPolice.Visible(RageUIPolice.NextMenu, true)
                    RageUIPolice.CurrentMenu.Controls.Select.Active = false
                    RageUIPolice.NextMenu = nil
                    RageUIPolice.LastControl = false
                end
            end
        end
    end
end

---ItemsDescription
---@param CurrentMenu table
---@param Description string
---@param Selected boolean
---@return nil
---@public
function RageUIPolice.ItemsDescription(CurrentMenu, Description, Selected)
    ---@type table
    if Description ~= "" or Description ~= nil then
        local SettingsDescription = RageUIPolice.Settings.Items.Description;
        if Selected and CurrentMenu.Description ~= Description then
            CurrentMenu.Description = Description or nil
            ---@type number
            local DescriptionLineCount = GetLineCount(CurrentMenu.Description, CurrentMenu.X + SettingsDescription.Text.X, CurrentMenu.Y + SettingsDescription.Text.Y + CurrentMenu.SubtitleHeight + RageUIPolice.ItemOffset, 0, SettingsDescription.Text.Scale, 255, 255, 255, 255, nil, false, false, SettingsDescription.Background.Width + (CurrentMenu.WidthOffset - 5.0))
            if DescriptionLineCount > 1 then
                CurrentMenu.DescriptionHeight = SettingsDescription.Background.Height * DescriptionLineCount
            else
                CurrentMenu.DescriptionHeight = SettingsDescription.Background.Height + 7
            end
        end
    end
end

---MouseBounds
---@param CurrentMenu table
---@param Selected boolean
---@param Option number
---@param SettingsButton table
---@return boolean
---@public
function RageUIPolice.ItemsMouseBounds(CurrentMenu, Selected, Option, SettingsButton)
    ---@type boolean
    local Hovered = false
    Hovered = RageUIPolice.IsMouseInBounds(CurrentMenu.X + CurrentMenu.SafeZoneSize.X, CurrentMenu.Y + SettingsButton.Rectangle.Y + CurrentMenu.SafeZoneSize.Y + CurrentMenu.SubtitleHeight + RageUIPolice.ItemOffset, SettingsButton.Rectangle.Width + CurrentMenu.WidthOffset, SettingsButton.Rectangle.Height)
    if Hovered and not Selected then
        RenderRectangle(CurrentMenu.X, CurrentMenu.Y + SettingsButton.Rectangle.Y + CurrentMenu.SubtitleHeight + RageUIPolice.ItemOffset, SettingsButton.Rectangle.Width + CurrentMenu.WidthOffset, SettingsButton.Rectangle.Height, 255, 255, 255, 20)
        if CurrentMenu.Controls.Click.Active then
            CurrentMenu.Index = Option
            local Audio = RageUIPolice.Settings.Audio
            RageUIPolice.PlaySound(Audio[Audio.Use].Error.audioName, Audio[Audio.Use].Error.audioRef)
        end
    end
    return Hovered;
end

---ItemsSafeZone
---@param CurrentMenu table
---@return nil
---@public
function RageUIPolice.ItemsSafeZone(CurrentMenu)
    if not CurrentMenu.SafeZoneSize then
        CurrentMenu.SafeZoneSize = { X = 0, Y = 0 }
        if CurrentMenu.Safezone then
            CurrentMenu.SafeZoneSize = RageUIPolice.GetSafeZoneBounds()
            SetScriptGfxAlign(76, 84)
            SetScriptGfxAlignParams(0, 0, 0, 0)
        end
    end
end

function RageUIPolice.CurrentIsEqualTo(Current, To, Style, DefaultStyle)
    if (Current == To) then
        return Style;
    else
        return DefaultStyle or {};
    end
end

function RageUIPolice.RefreshPagination()
    if (RageUIPolice.CurrentMenu ~= nil) then
        if (RageUIPolice.CurrentMenu.Index > 10) then
            local offset = RageUIPolice.CurrentMenu.Index - 10
            RageUIPolice.CurrentMenu.Pagination.Minimum = 1 + offset
            RageUIPolice.CurrentMenu.Pagination.Maximum = 10 + offset
        else
            RageUIPolice.CurrentMenu.Pagination.Minimum = 1
            RageUIPolice.CurrentMenu.Pagination.Maximum = 10
        end
    end
end

function RageUIPolice.IsVisible(menu, header, glare, instructional, items, panels)
    if (RageUIPolice.Visible(menu)) then
        if (header == true) then
            RageUIPolice.Banner(true, glare or false)
        end
        RageUIPolice.Subtitle()
        if (items ~= nil) then
            items()
        end
        RageUIPolice.Background();
        RageUIPolice.Navigation();
        RageUIPolice.Description();
        if (panels ~= nil) then
            panels()
        end
        RageUIPolice.Render(instructional or false)
    end
end


---CreateWhile
---@param wait number
---@param menu table
---@param key number
---@param closure function
---@return void
---@public
function RageUIPolice.CreateWhile(wait, menu, key, closure)
    Citizen.CreateThread(function()
        while (true) do
            Citizen.Wait(wait or 0.1)

            if (key ~= nil) then
                if IsControlJustPressed(1, key) then
                    RageUIPolice.Visible(menu, not RageUIPolice.Visible(menu))
                end
            end

            closure()
        end
    end)
end

