function RageUIjh.SetScaleformParams(scaleform, data)
    data = data or {}
    for k, v in pairs(data) do
        PushScaleformMovieFunction(scaleform, v.name)
        if v.param then
            for _, par in pairs(v.param) do
                if math.type(par) == "integer" then
                    PushScaleformMovieFunctionParameterInt(par)
                elseif type(par) == "boolean" then
                    PushScaleformMovieFunctionParameterBool(par)
                elseif math.type(par) == "float" then
                    PushScaleformMovieFunctionParameterFloat(par)
                elseif type(par) == "string" then
                    PushScaleformMovieFunctionParameterString(par)
                end
            end
        end
        if v.func then
            v.func()
        end
        PopScaleformMovieFunctionVoid()
    end
end

---Visible
---@param Menu function
---@param Value boolean
---@return table
---@public
function RageUIjh.Visible(Menu, Value)
    if Menu ~= nil then
        if Menu() then
            if type(Value) == "boolean" then
                if Value then
                    if RageUIjh.CurrentMenu ~= nil then
			if RageUIjh.CurrentMenu.Closed ~= nil then
                            RageUIjh.CurrentMenu.Closed()
                        end
                        RageUIjh.CurrentMenu.Open = not Value
                    end
                    Menu:UpdateInstructionalButtons(Value);
                    Menu:UpdateCursorStyle();
                    RageUIjh.CurrentMenu = Menu
                    menuOpen = true
                else
                    menuOpen = false
                    RageUIjh.CurrentMenu = nil
                end
                Menu.Open = Value
                RageUIjh.Options = 0
                RageUIjh.ItemOffset = 0
                RageUIjh.LastControl = false
            else
                return Menu.Open
            end
        end
    end
end

function RageUIjh.CloseAll()
    menuOpen = false
    if RageUIjh.CurrentMenu ~= nil then
        local parent = RageUIjh.CurrentMenu.Parent
        while parent ~= nil do
            parent.Index = 1
            parent.Pagination.Minimum = 1
            parent.Pagination.Maximum = 10
            parent = parent.Parent
        end
        RageUIjh.CurrentMenu.Index = 1
        RageUIjh.CurrentMenu.Pagination.Minimum = 1
        RageUIjh.CurrentMenu.Pagination.Maximum = 10
        RageUIjh.CurrentMenu.Open = false
        RageUIjh.CurrentMenu = nil
    end
    RageUIjh.Options = 0
    RageUIjh.ItemOffset = 0
    ResetScriptGfxAlign()
end

