function RageUIjh.Subtitle()
    if RageUIjh.CurrentMenu ~= nil then
        if RageUIjh.CurrentMenu() then
            RageUIjh.ItemsSafeZone(RageUIjh.CurrentMenu)
            if RageUIjh.CurrentMenu.Subtitle ~= "" then
                RenderRectangle(RageUIjh.CurrentMenu.X, RageUIjh.CurrentMenu.Y + RageUIjh.ItemOffset, RageUIjh.Settings.Items.Subtitle.Background.Width + RageUIjh.CurrentMenu.WidthOffset, RageUIjh.Settings.Items.Subtitle.Background.Height + RageUIjh.CurrentMenu.SubtitleHeight, 0, 0, 0, 255)
