                if RageUIjh.CurrentMenu.Index > RageUIjh.CurrentMenu.Options or RageUIjh.CurrentMenu.Index < 0 then
                    RageUIjh.CurrentMenu.Index = 1
                end
                RageUIjh.RefreshPagination()
