                    RenderText(RageUIjh.CurrentMenu.PageCounterColour .. RageUIjh.CurrentMenu.Index .. " / " .. RageUIjh.CurrentMenu.Options, RageUIjh.CurrentMenu.X + RageUIjh.Settings.Items.Subtitle.PreText.X + RageUIjh.CurrentMenu.WidthOffset, RageUIjh.CurrentMenu.Y + RageUIjh.Settings.Items.Subtitle.PreText.Y + RageUIjh.ItemOffset, 0, RageUIjh.Settings.Items.Subtitle.PreText.Scale, 245, 245, 245, 255, 2)
