---Render
---@param instructionalButton boolean
---@return nil
---@public
function RageUIjh.Render(instructionalButton)
    if RageUIjh.CurrentMenu ~= nil then
        if RageUIjh.CurrentMenu() then
            if RageUIjh.CurrentMenu.Safezone then
                ResetScriptGfxAlign()
            end
            if (instructionalButton) then
                DrawScaleformMovieFullscreen(RageUIjh.CurrentMenu.InstructionalScaleform, 255, 255, 255, 255, 0)
            end
            RageUIjh.CurrentMenu.Options = RageUIjh.Options
            RageUIjh.CurrentMenu.SafeZoneSize = nil
            RageUIjh.Controls()
            RageUIjh.Options = 0
            RageUIjh.StatisticPanelCount = 0
            RageUIjh.ItemOffset = 0
            if RageUIjh.CurrentMenu.Controls.Back.Enabled and RageUIjh.CurrentMenu.Closable then
                if RageUIjh.CurrentMenu.Controls.Back.Pressed then
                    RageUIjh.CurrentMenu.Controls.Back.Pressed = false
                    local Audio = RageUIjh.Settings.Audio
                    RageUIjh.PlaySound(Audio[Audio.Use].Back.audioName, Audio[Audio.Use].Back.audioRef)
                    collectgarbage()
                    if RageUIjh.CurrentMenu.Closed ~= nil then
                        RageUIjh.CurrentMenu.Closed()
                    end
                    if RageUIjh.CurrentMenu.Parent ~= nil then
                        if RageUIjh.CurrentMenu.Parent() then
                            RageUIjh.NextMenu = RageUIjh.CurrentMenu.Parent
                            RageUIjh.CurrentMenu:UpdateCursorStyle()
                        else
                            --print('xxx') Debug print
                            RageUIjh.NextMenu = nil
                            RageUIjh.Visible(RageUIjh.CurrentMenu, false)
                        end
                    else
                        --print('zz') Debug print
                        RageUIjh.NextMenu = nil
                        RageUIjh.Visible(RageUIjh.CurrentMenu, false)
                    end
                end
            end
            if RageUIjh.NextMenu ~= nil then
                if RageUIjh.NextMenu() then
                    RageUIjh.Visible(RageUIjh.CurrentMenu, false)
                    RageUIjh.Visible(RageUIjh.NextMenu, true)
                    RageUIjh.CurrentMenu.Controls.Select.Active = false
                    RageUIjh.NextMenu = nil
                    RageUIjh.LastControl = false
                end
            end
        end
    end
end

---ItemsDescription
---@param CurrentMenu table
---@param Description string
---@param Selected boolean
---@return nil
---@public
function RageUIjh.ItemsDescription(CurrentMenu, Description, Selected)
    ---@type table
    if Description ~= "" or Description ~= nil then
        local SettingsDescription = RageUIjh.Settings.Items.Description;
        if Selected and CurrentMenu.Description ~= Description then
            CurrentMenu.Description = Description or nil
            ---@type number
            local DescriptionLineCount = GetLineCount(CurrentMenu.Description, CurrentMenu.X + SettingsDescription.Text.X, CurrentMenu.Y + SettingsDescription.Text.Y + CurrentMenu.SubtitleHeight + RageUIjh.ItemOffset, 0, SettingsDescription.Text.Scale, 255, 255, 255, 255, nil, false, false, SettingsDescription.Background.Width + (CurrentMenu.WidthOffset - 5.0))
            if DescriptionLineCount > 1 then
                CurrentMenu.DescriptionHeight = SettingsDescription.Background.Height * DescriptionLineCount
            else
                CurrentMenu.DescriptionHeight = SettingsDescription.Background.Height + 7
            end
        end
    end
end

---MouseBounds
---@param CurrentMenu table
---@param Selected boolean
---@param Option number
---@param SettingsButton table
---@return boolean
---@public
function RageUIjh.ItemsMouseBounds(CurrentMenu, Selected, Option, SettingsButton)
    ---@type boolean
    local Hovered = false
    Hovered = RageUIjh.IsMouseInBounds(CurrentMenu.X + CurrentMenu.SafeZoneSize.X, CurrentMenu.Y + SettingsButton.Rectangle.Y + CurrentMenu.SafeZoneSize.Y + CurrentMenu.SubtitleHeight + RageUIjh.ItemOffset, SettingsButton.Rectangle.Width + CurrentMenu.WidthOffset, SettingsButton.Rectangle.Height)
    if Hovered and not Selected then
        RenderRectangle(CurrentMenu.X, CurrentMenu.Y + SettingsButton.Rectangle.Y + CurrentMenu.SubtitleHeight + RageUIjh.ItemOffset, SettingsButton.Rectangle.Width + CurrentMenu.WidthOffset, SettingsButton.Rectangle.Height, 255, 255, 255, 20)
        if CurrentMenu.Controls.Click.Active then
            CurrentMenu.Index = Option
            local Audio = RageUIjh.Settings.Audio
            RageUIjh.PlaySound(Audio[Audio.Use].Error.audioName, Audio[Audio.Use].Error.audioRef)
        end
    end
    return Hovered;
end

---ItemsSafeZone
---@param CurrentMenu table
---@return nil
---@public
function RageUIjh.ItemsSafeZone(CurrentMenu)
    if not CurrentMenu.SafeZoneSize then
        CurrentMenu.SafeZoneSize = { X = 0, Y = 0 }
        if CurrentMenu.Safezone then
            CurrentMenu.SafeZoneSize = RageUIjh.GetSafeZoneBounds()
            SetScriptGfxAlign(76, 84)
            SetScriptGfxAlignParams(0, 0, 0, 0)
        end
    end
end

function RageUIjh.CurrentIsEqualTo(Current, To, Style, DefaultStyle)
    if (Current == To) then
        return Style;
    else
        return DefaultStyle or {};
    end
end

function RageUIjh.RefreshPagination()
    if (RageUIjh.CurrentMenu ~= nil) then
        if (RageUIjh.CurrentMenu.Index > 10) then
            local offset = RageUIjh.CurrentMenu.Index - 10
            RageUIjh.CurrentMenu.Pagination.Minimum = 1 + offset
            RageUIjh.CurrentMenu.Pagination.Maximum = 10 + offset
        else
            RageUIjh.CurrentMenu.Pagination.Minimum = 1
            RageUIjh.CurrentMenu.Pagination.Maximum = 10
        end
    end
end

function RageUIjh.IsVisible(menu, header, glare, instructional, items, panels)
    if (RageUIjh.Visible(menu)) then
        if (header == true) then
            RageUIjh.Banner(true, glare or false)
        end
        RageUIjh.Subtitle()
        if (items ~= nil) then
            items()
        end
        RageUIjh.Background();
        RageUIjh.Navigation();
        RageUIjh.Description();
        if (panels ~= nil) then
            panels()
        end
        RageUIjh.Render(instructional or false)
    end
end


---CreateWhile
---@param wait number
---@param menu table
---@param key number
---@param closure function
---@return void
---@public
function RageUIjh.CreateWhile(wait, menu, key, closure)
    Citizen.CreateThread(function()
        while (true) do
            Citizen.Wait(wait or 0.1)

            if (key ~= nil) then
                if IsControlJustPressed(1, key) then
                    RageUIjh.Visible(menu, not RageUIjh.Visible(menu))
                end
            end

            closure()
        end
    end)
end

