function RageUIjh.SetStyleAudio(StyleAudio)
    RageUIjh.Settings.Audio.Use = StyleAudio or "RageUIjh"
end

function RageUIjh.GetStyleAudio()
    return RageUIjh.Settings.Audio.Use or "RageUIjh"
end

