function RageUIc.SetScaleformParams(scaleform, data)
    data = data or {}
    for k, v in pairs(data) do
        PushScaleformMovieFunction(scaleform, v.name)
        if v.param then
            for _, par in pairs(v.param) do
                if math.type(par) == "integer" then
                    PushScaleformMovieFunctionParameterInt(par)
                elseif type(par) == "boolean" then
                    PushScaleformMovieFunctionParameterBool(par)
                elseif math.type(par) == "float" then
                    PushScaleformMovieFunctionParameterFloat(par)
                elseif type(par) == "string" then
                    PushScaleformMovieFunctionParameterString(par)
                end
            end
        end
        if v.func then
            v.func()
        end
        PopScaleformMovieFunctionVoid()
    end
end

---Visible
---@param Menu function
---@param Value boolean
---@return table
---@public
function RageUIc.Visible(Menu, Value)
    if Menu ~= nil then
        if Menu() then
            if type(Value) == "boolean" then
                if Value then
                    if RageUIc.CurrentMenu ~= nil then
                        RageUIc.CurrentMenu.Closed()
                        RageUIc.CurrentMenu.Open = not Value
                    end
                    Menu:UpdateInstructionalButtons(Value);
                    Menu:UpdateCursorStyle();
                    RageUIc.CurrentMenu = Menu
                    menuOpen = true
                else
                    menuOpen = false
                    RageUIc.CurrentMenu = nil
                end
                Menu.Open = Value
                RageUIc.Options = 0
                RageUIc.ItemOffset = 0
                RageUIc.LastControl = false
            else
                return Menu.Open
            end
        end
    end
end 

function RageUIc.CloseAll()
    menuOpen = false

    if RageUIc.CurrentMenu ~= nil then
        local parent = RageUIc.CurrentMenu.Parent
        while parent ~= nil do
            parent.Index = 1
            parent.Pagination.Minimum = 1
            parent.Pagination.Maximum = 10
            parent = parent.Parent
        end
        RageUIc.CurrentMenu.Index = 1
        RageUIc.CurrentMenu.Pagination.Minimum = 1
        RageUIc.CurrentMenu.Pagination.Maximum = 10
        RageUIc.CurrentMenu.Open = false
        RageUIc.CurrentMenu = nil
    end
    RageUIc.Options = 0
    RageUIc.ItemOffset = 0
    ResetScriptGfxAlign()
end

