---Render
---@param instructionalButton boolean
---@return nil
---@public
function RageUIc.Render(instructionalButton)
    if RageUIc.CurrentMenu ~= nil then
        if RageUIc.CurrentMenu() then
            if RageUIc.CurrentMenu.Safezone then
                ResetScriptGfxAlign()
            end
            if (instructionalButton) then
                DrawScaleformMovieFullscreen(RageUIc.CurrentMenu.InstructionalScaleform, 255, 255, 255, 255, 0)
            end
            RageUIc.CurrentMenu.Options = RageUIc.Options
            RageUIc.CurrentMenu.SafeZoneSize = nil
            RageUIc.Controls()
            RageUIc.Options = 0
            RageUIc.StatisticPanelCount = 0
            RageUIc.ItemOffset = 0
            if RageUIc.CurrentMenu.Controls.Back.Enabled and RageUIc.CurrentMenu.Closable then
                if RageUIc.CurrentMenu.Controls.Back.Pressed then
                    RageUIc.CurrentMenu.Controls.Back.Pressed = false
                    local Audio = RageUIc.Settings.Audio
                    RageUIc.PlaySound(Audio[Audio.Use].Back.audioName, Audio[Audio.Use].Back.audioRef)
                    if RageUIc.CurrentMenu.Closed ~= nil then
                        collectgarbage()
                        RageUIc.CurrentMenu.Closed()
                    end
                    if RageUIc.CurrentMenu.Parent ~= nil then
                        if RageUIc.CurrentMenu.Parent() then
                            RageUIc.NextMenu = RageUIc.CurrentMenu.Parent
                            RageUIc.CurrentMenu:UpdateCursorStyle()
                        else
                            --print('xxx') Debug print
                            RageUIc.NextMenu = nil
                            RageUIc.Visible(RageUIc.CurrentMenu, false)
                        end
                    else
                        --print('zz') Debug print
                        RageUIc.NextMenu = nil
                        RageUIc.Visible(RageUIc.CurrentMenu, false)
                    end
                end
            end
            if RageUIc.NextMenu ~= nil then
                if RageUIc.NextMenu() then
                    RageUIc.Visible(RageUIc.CurrentMenu, false)
                    RageUIc.Visible(RageUIc.NextMenu, true)
                    RageUIc.CurrentMenu.Controls.Select.Active = false
                    RageUIc.NextMenu = nil
                    RageUIc.LastControl = false
                end
            end
        end
    end
end

---ItemsDescription
---@param CurrentMenu table
---@param Description string
---@param Selected boolean
---@return nil
---@public
function RageUIc.ItemsDescription(CurrentMenu, Description, Selected)
    ---@type table
    if Description ~= "" or Description ~= nil then
        local SettingsDescription = RageUIc.Settings.Items.Description;
        if Selected and CurrentMenu.Description ~= Description then
            CurrentMenu.Description = Description or nil
            ---@type number
            local DescriptionLineCount = GetLineCount(CurrentMenu.Description, CurrentMenu.X + SettingsDescription.Text.X, CurrentMenu.Y + SettingsDescription.Text.Y + CurrentMenu.SubtitleHeight + RageUIc.ItemOffset, 0, SettingsDescription.Text.Scale, 255, 255, 255, 255, nil, false, false, SettingsDescription.Background.Width + (CurrentMenu.WidthOffset - 5.0))
            if DescriptionLineCount > 1 then
                CurrentMenu.DescriptionHeight = SettingsDescription.Background.Height * DescriptionLineCount
            else
                CurrentMenu.DescriptionHeight = SettingsDescription.Background.Height + 7
            end
        end
    end
end

---MouseBounds
---@param CurrentMenu table
---@param Selected boolean
---@param Option number
---@param SettingsButton table
---@return boolean
---@public
function RageUIc.ItemsMouseBounds(CurrentMenu, Selected, Option, SettingsButton)
    ---@type boolean
    local Hovered = false
    Hovered = RageUIc.IsMouseInBounds(CurrentMenu.X + CurrentMenu.SafeZoneSize.X, CurrentMenu.Y + SettingsButton.Rectangle.Y + CurrentMenu.SafeZoneSize.Y + CurrentMenu.SubtitleHeight + RageUIc.ItemOffset, SettingsButton.Rectangle.Width + CurrentMenu.WidthOffset, SettingsButton.Rectangle.Height)
    if Hovered and not Selected then
        RenderRectangle(CurrentMenu.X, CurrentMenu.Y + SettingsButton.Rectangle.Y + CurrentMenu.SubtitleHeight + RageUIc.ItemOffset, SettingsButton.Rectangle.Width + CurrentMenu.WidthOffset, SettingsButton.Rectangle.Height, 255, 255, 255, 20)
        if CurrentMenu.Controls.Click.Active then
            CurrentMenu.Index = Option
            local Audio = RageUIc.Settings.Audio
            RageUIc.PlaySound(Audio[Audio.Use].Error.audioName, Audio[Audio.Use].Error.audioRef)
        end
    end
    return Hovered;
end

---ItemsSafeZone
---@param CurrentMenu table
---@return nil
---@public
function RageUIc.ItemsSafeZone(CurrentMenu)
    if not CurrentMenu.SafeZoneSize then
        CurrentMenu.SafeZoneSize = { X = 0, Y = 0 }
        if CurrentMenu.Safezone then
            CurrentMenu.SafeZoneSize = RageUIc.GetSafeZoneBounds()
            SetScriptGfxAlign(76, 84)
            SetScriptGfxAlignParams(0, 0, 0, 0)
        end
    end
end

function RageUIc.CurrentIsEqualTo(Current, To, Style, DefaultStyle)
    if (Current == To) then
        return Style;
    else
        return DefaultStyle or {};
    end
end

function RageUIc.RefreshPagination()
    if (RageUIc.CurrentMenu ~= nil) then
        if (RageUIc.CurrentMenu.Index > 10) then
            local offset = RageUIc.CurrentMenu.Index - 10
            RageUIc.CurrentMenu.Pagination.Minimum = 1 + offset
            RageUIc.CurrentMenu.Pagination.Maximum = 10 + offset
        else
            RageUIc.CurrentMenu.Pagination.Minimum = 1
            RageUIc.CurrentMenu.Pagination.Maximum = 10
        end
    end
end

function RageUIc.IsVisible(menu, header, glare, instructional, items, panels)
    if (RageUIc.Visible(menu)) then
        if (header == true) then
            RageUIc.Banner(true, glare or false)
        end
        RageUIc.Subtitle()
        if (items ~= nil) then
            items()
        end
        RageUIc.Background();
        RageUIc.Navigation();
        RageUIc.Description();
        if (panels ~= nil) then
            panels()
        end
        RageUIc.Render(instructional or false)
    end
end


---CreateWhile
---@param wait number
---@param menu table
---@param key number
---@param closure function
---@return void
---@public
function RageUIc.CreateWhile(wait, menu, key, closure)
    Citizen.CreateThread(function()
        while (true) do
            Citizen.Wait(wait or 0.1)

            if (key ~= nil) then
                if IsControlJustPressed(1, key) then
                    RageUIc.Visible(menu, not RageUIc.Visible(menu))
                end
            end

            closure()
        end
    end)
end

