---Render
---@param instructionalButton boolean
---@return nil
---@public
function RageUIv3.Render(instructionalButton)
    if RageUIv3.CurrentMenu ~= nil then
        if RageUIv3.CurrentMenu() then
            if RageUIv3.CurrentMenu.Safezone then
                ResetScriptGfxAlign()
            end
            if (instructionalButton) then
                DrawScaleformMovieFullscreen(RageUIv3.CurrentMenu.InstructionalScaleform, 255, 255, 255, 255, 0)
            end
            RageUIv3.CurrentMenu.Options = RageUIv3.Options
            RageUIv3.CurrentMenu.SafeZoneSize = nil
            RageUIv3.Controls()
            RageUIv3.Options = 0
            RageUIv3.StatisticPanelCount = 0
            RageUIv3.ItemOffset = 0
            if RageUIv3.CurrentMenu.Controls.Back.Enabled and RageUIv3.CurrentMenu.Closable then
                if RageUIv3.CurrentMenu.Controls.Back.Pressed then
                    RageUIv3.CurrentMenu.Controls.Back.Pressed = false
                    local Audio = RageUIv3.Settings.Audio
                    RageUIv3.PlaySound(Audio[Audio.Use].Back.audioName, Audio[Audio.Use].Back.audioRef)
                    if RageUIv3.CurrentMenu.Closed ~= nil then
                        RageUIv3.CurrentMenu.Closed()
                    end
                    if RageUIv3.CurrentMenu.Parent ~= nil then
                        if RageUIv3.CurrentMenu.Parent() then
                            RageUIv3.NextMenu = RageUIv3.CurrentMenu.Parent
                        else
                            RageUIv3.NextMenu = nil
                            RageUIv3.Visible(RageUIv3.CurrentMenu, false)
                        end
                    else
                        RageUIv3.NextMenu = nil
                        RageUIv3.Visible(RageUIv3.CurrentMenu, false)
                    end
                end
            end
            if RageUIv3.NextMenu ~= nil then
                if RageUIv3.NextMenu() then
                    RageUIv3.Visible(RageUIv3.CurrentMenu, false)
                    RageUIv3.Visible(RageUIv3.NextMenu, true)
                    RageUIv3.CurrentMenu.Controls.Select.Active = false
                    RageUIv3.NextMenu = nil
                    RageUIv3.LastControl = false
                end
            end
        end
    end
end

---ItemsDescription
---@param CurrentMenu table
---@param Description string
---@param Selected boolean
---@return nil
---@public
function RageUIv3.ItemsDescription(CurrentMenu, Description, Selected)
    ---@type table
    if Description ~= "" or Description ~= nil then
        local SettingsDescription = RageUIv3.Settings.Items.Description;
        if Selected and CurrentMenu.Description ~= Description then
            CurrentMenu.Description = Description or nil
            ---@type number
            local DescriptionLineCount = GetLineCount(CurrentMenu.Description, CurrentMenu.X + SettingsDescription.Text.X, CurrentMenu.Y + SettingsDescription.Text.Y + CurrentMenu.SubtitleHeight + RageUIv3.ItemOffset, 0, SettingsDescription.Text.Scale, 255, 255, 255, 255, nil, false, false, SettingsDescription.Background.Width + CurrentMenu.WidthOffset)
            if DescriptionLineCount > 1 then
                CurrentMenu.DescriptionHeight = SettingsDescription.Background.Height * DescriptionLineCount
            else
                CurrentMenu.DescriptionHeight = SettingsDescription.Background.Height + 7
            end
        end
    end
end

---MouseBounds
---@param CurrentMenu table
---@param Selected boolean
---@param Option number
---@param SettingsButton table
---@return boolean
---@public
function RageUIv3.ItemsMouseBounds(CurrentMenu, Selected, Option, SettingsButton)
    ---@type boolean
    local Hovered = false
    Hovered = RageUIv3.IsMouseInBounds(CurrentMenu.X + CurrentMenu.SafeZoneSize.X, CurrentMenu.Y + SettingsButton.Rectangle.Y + CurrentMenu.SafeZoneSize.Y + CurrentMenu.SubtitleHeight + RageUIv3.ItemOffset, SettingsButton.Rectangle.Width + CurrentMenu.WidthOffset, SettingsButton.Rectangle.Height)
    if Hovered and not Selected then
        RenderRectangle(CurrentMenu.X, CurrentMenu.Y + SettingsButton.Rectangle.Y + CurrentMenu.SubtitleHeight + RageUIv3.ItemOffset, SettingsButton.Rectangle.Width + CurrentMenu.WidthOffset, SettingsButton.Rectangle.Height, 255, 255, 255, 20)
        if CurrentMenu.Controls.Click.Active then
            CurrentMenu.Index = Option
            local Audio = RageUIv3.Settings.Audio
            RageUIv3.PlaySound(Audio[Audio.Use].Error.audioName, Audio[Audio.Use].Error.audioRef)
        end
    end
    return Hovered;
end

---ItemsSafeZone
---@param CurrentMenu table
---@return nil
---@public
function RageUIv3.ItemsSafeZone(CurrentMenu)
    if not CurrentMenu.SafeZoneSize then
        CurrentMenu.SafeZoneSize = { X = 0, Y = 0 }
        if CurrentMenu.Safezone then
            CurrentMenu.SafeZoneSize = RageUIv3.GetSafeZoneBounds()
            SetScriptGfxAlign(76, 84)
            SetScriptGfxAlignParams(0, 0, 0, 0)
        end
    end
end

function RageUIv3.CurrentIsEqualTo(Current, To, Style, DefaultStyle)
    if (Current == To) then
        return Style;
    else
        return DefaultStyle or {};
    end
end

function RageUIv3.RefreshPagination()
    if (RageUIv3.CurrentMenu ~= nil) then
        if (RageUIv3.CurrentMenu.Index > 10) then
            local offset = RageUIv3.CurrentMenu.Index - 10
            RageUIv3.CurrentMenu.Pagination.Minimum = 1 + offset
            RageUIv3.CurrentMenu.Pagination.Maximum = 10 + offset
        else
            RageUIv3.CurrentMenu.Pagination.Minimum = 1
            RageUIv3.CurrentMenu.Pagination.Maximum = 10
        end
    end
end

function RageUIv3.IsVisible(menu, header, glare, instructional, items, panels)
    if (RageUIv3.Visible(menu)) then
        if (header == true) then
            RageUIv3.Banner(true, glare or false)
        end
        RageUIv3.Subtitle()
        if (items ~= nil) then
            items()
        end
        RageUIv3.Background();
        RageUIv3.Navigation();
        RageUIv3.Description();
        if (panels ~= nil) then
            panels()
        end
        RageUIv3.Render(instructional or false)
    end
end

